package basic_PF;
import java.util.Scanner;

public class Seriesof2till10 {
	public static void show(){
		for(int i=0;i<=10;i++){
	    	 if(i%2==0)
	    		 System.out.println(i);
		
	}
	}

	public static void main(String[] args) {
		// TODO Auto-generated method stub
     Scanner s = new Scanner(System.in);
     show();
     }
	}


